package org.learn.preparation.leetcode;

public class DuplicateSubstring {

    public String longestDupSubstring(String S) {

        if (S == null || S.length() == 0)
            return "";

        int len = S.length();

        // now we can try to use windowing
        int window = len - 1;  //banana len=6 window=4

        while (window > 0) {
            for (int j = 0; j < len - window; j++) {
                String originalString = S.substring(j, j + window);
                for (int i = j + 1; i <= len - window; i++) {
                    String currSubstring = S.substring(i, i + window);
                    if (originalString.equals(currSubstring)) {
                        return currSubstring;
                    }
                }
            }
            window--;
        }
        return "";
    }

    public static void main(String[] args) {
        DuplicateSubstring duplicateSubstring = new DuplicateSubstring();
        System.out.println("Result:" + duplicateSubstring.longestDupSubstring(""));
    }
}
